sys_script_include.script:
function validateEmailAddress(emailStr){
	// use Havascript coercion to guanantee emailStr is a string
	emailStr = emailStr + '';
	// Compare emailstr against the allowed sytnax as specified in the reigular expression
	// If emailstr has allowed syntax, return true, else return false
	if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else {
		return false;
	}
}
